import React, { useState, useEffect } from 'react';
import Fab from '@mui/material/Fab';
import ArrowUpwardOutlinedIcon from '@mui/icons-material/ArrowUpwardOutlined';

import theme from '../../../theme';

const Footer = () => {
    const [BtnStatus, setBtnStatus] = useState(false); // 버튼 상태

    const handleFollow = () => {
        if ((window.scrollY || window.pageYOffset) > 300) {
            setBtnStatus(true);
        } else {
            setBtnStatus(false);
        }
    };
    const handleTop = () => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth',
        });
    };
    useEffect(() => {
        const watch = () => {
            window.addEventListener('scroll', handleFollow);
        };
        watch();
        return () => {
            window.removeEventListener('scroll', handleFollow);
        };
    });
    return (
        <div
            className="flex justify-center w-full items-center h-10 border-t border-gray-400"
            style={{ backgroundColor: theme.palette.secondary.main }}
        >
            <div className="flex w-full items-center px-4">
                <div className="text-xs text-white">
                    Copyright ⓒ 제로트러스트 클라우드 보안 연구센터. All Rights Reserved.
                </div>
                {BtnStatus && (
                    <Fab
                        className="!fixed bottom-5 right-5 2xl:right-[13.5%] !bg-primary !hover:bg-secondary !text-white"
                        onClick={handleTop}
                    >
                        <ArrowUpwardOutlinedIcon />
                    </Fab>
                )}
            </div>
        </div>
    );
};

export default Footer;
